mapper.select:
SELECT
count(*)
FROM t_role_menu


		and ${searchStr}


		and role_id = #{roleId}

SELECT tmi.id, CASE tmi.parent_id WHEN -1 THEN NULL ELSE tmi.parent_id END AS parent_id, trm.menu_id, tmi.menu_name, tmi.menu_level -1 as menu_level, tmi.icon, tmi.url, CASE WHEN tmi.url is NULL THEN false WHEN tmi.url ='' THEN false ELSE true END as menu_leaf, trm.operation, tmi.sort_order, tmi.operation_all,
true AS expanded
false AS expanded
FROM t_role_menu trm INNER JOIN t_role_info tri ON tri.id = trm.role_id INNER JOIN t_menu_info tmi ON tmi.id = trm.menu_id
and ${searchStr}
and trm.role_id = #{roleId}
and tmi.menu_type = #{type,jdbcType=INTEGER} AND tmi.del_flag = 0
${orderStr}
ORDER BY tmi.parent_id, tmi.sort_order

SELECT tmi.id, CASE tmi.parent_id WHEN -1 THEN NULL ELSE tmi.parent_id END AS parent_id, CASE WHEN trm.role_id IS null THEN false ELSE true END AS has_role, tmi.id as menu_id, tmi.menu_name, tmi.menu_level -1 as menu_level, tmi.icon, tmi.url, CASE WHEN tmi.url is NULL THEN false WHEN tmi.url ='' THEN false ELSE true END as menu_leaf, trm.operation, tmi.sort_order, tmi.operation_all,
true AS expanded
false AS expanded
FROM (SELECT * FROM t_role_menu
and role_id = #{roleId}
) AS trm LEFT JOIN t_role_info tri ON tri.id = trm.role_id RIGHT JOIN t_menu_info tmi ON tmi.id = trm.menu_id
and ${searchStr}
and tmi.menu_type = #{type,jdbcType=INTEGER} AND tmi.del_flag = 0
${orderStr}
ORDER BY tmi.parent_id, tmi.sort_order
