mapper.insert:
insert into t_user_role (user_id, role_id, create_time, 
  create_user, update_time, update_user
  )
values (#{userId,jdbcType=INTEGER}, #{roleId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}
  )

mapper.select:
SELECT ur.user_id, user_name, real_name, ur.role_id, role_name, ri.description, ri.del_flag, ur.create_time, ur.create_user, ur.update_time, ur.update_user FROM t_user_role ur INNER JOIN t_user_info ui on ur.user_id = ui.id INNER JOIN t_role_info ri on ur.role_id = ri.id
and ${searchStr}
${orderStr}
ORDER BY ur.user_id, ur.role_id
